import React, { useState } from "react";
import { Row, Col, Card, Input, Button } from "react-materialize";
import { useHistory } from "react-router-dom";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { loginAction } from "../../actions";
import { login } from "../../api";
import FlashMessage from "react-flash-message";

const Login = (props) => {
  const history = useHistory(),
    { updateHeader, loginAction } = props,
    [loginData, setLoginData] = useState({ email: "", password: "" });

  const submitLogin = async () => {
    const response = await login(loginData);
    if (response.status === 200) {
      loginAction(response.data.token);
      history.push("/users");
      updateHeader();
    }
  };

  return (
    <Row>
      <FlashMessage duration={4000}>
        <div className="notifications-component">
          <div class="notification-container--top-right">
            <div class="notification">
              <div class="animated fadeIn notification__item notification__item--default">
                <div class="notification__content">
                  <div class="notification__message">
                    All your messages have been logged
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </FlashMessage>
      <Col m={12} s={12}>
        <h5>Login</h5>
        <Card>
          <Row>
            <Input
              placeholder="endereÃ§o@email.com"
              type="email"
              label="Email"
              s={12}
              onChange={(e) =>
                setLoginData({ ...loginData, email: e.target.value })
              }
            />
            <Input
              placeholder="Senha"
              label="Senha"
              type="password"
              s={12}
              onChange={(e) =>
                setLoginData({ ...loginData, password: e.target.value })
              }
            />
            <Col s={12} m={12}>
              <Button
                waves="light"
                className="blue darken-2"
                onClick={submitLogin}
              >
                ENTRAR
              </Button>
            </Col>
          </Row>
        </Card>
      </Col>
    </Row>
  );
};

const mapStateToProps = (store) => ({
  token: store.clickState.token,
});

const mapDispatchToProps = (dispatch) =>
  bindActionCreators({ loginAction }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Login);
