import React, { useState, useEffect } from "react";
import { Row, Col, Card } from "react-materialize";
import { useStore } from "react-redux";
import axios from "axios";

const EditUser = ({ match }) => {
  const store = useStore(),
    [usersList, setUsersList] = useState([]),
    usersListHtml = [];

  useEffect(() => {
    const loadUser = async () => {
      const response = await axios.get(
        `https://reqres.in/api/users/${match.params.id}?delay=2`,
        {
          headers: {
            token: store.getState().clickState.token,
          },
        }
      );
      setUsersList(response.data);
    };

    loadUsers();
  }, [store]);

  const editUser = async (id) => {
    console.log(id);
  };

  if (usersList.data) {
    const usersListData = usersList.data;
    for (let i = 0; i < usersListData.length; i++) {
      usersListHtml.push(
        <li className="collection-item">
          <span
            className="text-link"
            onClick={() => editUser(usersListData[i].id)}
          >
            {usersListData[i].first_name} {usersListData[i].last_name}
          </span>
          <span className="right">X</span>
        </li>
      );
    }
  }

  return (
    <Row>
      <Col m={12} s={12}>
        <h5 className="subtitle">Usu√°rios</h5>
        <Card>
          <div>
            <ul class="collection">{usersListHtml}</ul>
          </div>
        </Card>
      </Col>
    </Row>
  );
};

export default EditUser;
