import React, { Component } from "react";
import "./App.scss";
import Header from "./components/header/header";
import Login from "./components/login/login";
import UserList from "./components/users/users";
import EditUser from "./components/edit-user/edit-users";
import NotFound from "./components/not-found/not-found";
import { Container } from "react-materialize";
import { Switch, Route } from "react-router-dom";
import FlashMessage from "react-flash-message";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogged: /^(\/users|\/users\/|\/users\/[1-9][0-2]*)$/.test(
        window.location.pathname
      ),
      notify: { message: "zile", show: false },
    };
  }

  render() {
    return (
      <div>
        {this.state.isLogged}
        <FlashMessage duration={999999999}>
          <div
            className={`notifications-component ${
              this.state.notify.show ? "show" : "hide"
            }`}
          >
            <div className="notification-container--top-right">
              <div className="notification">
                <div className="notification__item notification__item--default">
                  <div className="notification__content">
                    <div className="notification__message">
                      {this.state.notify.message}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </FlashMessage>
        <Header
          isLogged={this.state.isLogged}
          updateHeader={() => this.setState({ isLogged: false })}
        />
        <Container>
          <Switch>
            <Route
              exact
              path="/"
              render={() => (
                <Login
                  updateHeader={() => this.setState({ isLogged: true })}
                  zile={
                    (/* text */) =>
                      this.setState({ show: true, message: "asd" })
                  }
                />
              )}
            />
            <Route exact path="/users" component={UserList} />
            <Route path="/users/:id(\d+)" component={EditUser} />
            <Route path="*" component={NotFound} />
          </Switch>
        </Container>
      </div>
    );
  }
}

export default App;
