import React, { useState, useEffect } from "react";
import { Row, Col, Card } from "react-materialize";
import { useHistory } from "react-router-dom";
import { useStore } from "react-redux";
import { getUserList, deleteUserById } from "../../api";
import qS from "query-string";

const UserList = ({ location }) => {
  const history = useHistory(),
    store = useStore(),
    [usersList, setUsersList] = useState([]),
    usersListHtml = [];

  useEffect(() => {
    const currentPage = qS.parse(location.search).page;
    history.push(`/users?page=${currentPage}`);
    const loadUsers = async () => {
      const response = await getUserList(currentPage, store);
      setUsersList(response.data);
    };

    loadUsers();
  }, [store]);

  const editUser = (id) => {
    history.push(`/users/${id}`);
  };

  const deleteUser = async (id) => {
    await deleteUserById(id, store);
  };

  if (usersList?.data) {
    const usersListData = usersList.data;
    for (let i = 0; i < usersListData.length; i++) {
      usersListHtml.push(
        <li className="collection-item">
          <span
            className="text-link"
            onClick={() => editUser(usersListData[i].id)}
          >
            {usersListData[i].first_name} {usersListData[i].last_name}
          </span>
          <span
            className="right delete-icon"
            title="Deletar usuário"
            onClick={() => deleteUser(usersListData[i].id)}
          >
            &#10006;
          </span>
        </li>
      );
    }
  }

  return (
    <Row>
      <Col m={12} s={12}>
        <h5 className="subtitle">Usuários</h5>
        <Card>
          <div>
            <ul class="collection">{usersListHtml}</ul>
          </div>
        </Card>
      </Col>
    </Row>
  );
};

export default UserList;
