import React from "react";
import { Row, Col, Card, Input, Button } from "react-materialize";
import { useHistory } from "react-router-dom";
import axios from "axios";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { loginAction } from "../../actions";

const Login = (props) => {
  const history = useHistory();
  const { loginAction } = props;

  const submitLogin = async () => {
    const response = await axios.post(`https://reqres.in/api/login?delay=2`, {
      email: "eve.holt@reqres.in",
      password: "cityslicka",
    });
    if (response.status === 200) {
      loginAction(response.data.token);
      history.push("/users");
    }
  };

  return (
    <Row>
      <Col m={12} s={12}>
        <h5>Login</h5>
        <Card>
          <Row>
            <Input
              placeholder="endereÃ§o@email.com"
              type="email"
              label="Email"
              s={12}
            />
            <Input placeholder="Senha" label="Senha" type="password" s={12} />
            <Col s={12} m={12}>
              <Button
                waves="light"
                className="right grey darken-2"
                onClick={submitLogin}
              >
                ENTRAR
              </Button>
            </Col>
          </Row>
        </Card>
      </Col>
    </Row>
  );
};

const mapStateToProps = (store) => ({
  token: store.tokenApi.token,
});

const mapDispatchToProps = (dispatch) =>
  bindActionCreators({ loginAction }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Login);
