import React, { Component } from "react";
import "./App.scss";
import Header from "./components/header/header";
import Login from "./components/login/login";
import UserList from "./components/users/users";
import EditUser from "./components/edit-user/edit-users";
import NotFound from "./components/not-found/not-found";
import { Container } from "react-materialize";
import { Switch, Route } from "react-router-dom";
import FlashMessage from "react-flash-message";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogged: /^(\/users|\/users\/|\/users\/[1-9][0-2]*)$/.test(
        window.location.pathname
      ),
    };
  }

  render() {
    return (
      <div>
        <FlashMessage duration={4000}>
          <div className="notifications-component">
            <div class="notification-container--top-right">
              <div class="notification">
                <div class="animated notification__item notification__item--default">
                  <div class="notification__content">
                    <div class="notification__message">
                      All your messages have been logged
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </FlashMessage>
        <Header
          isLogged={this.state.isLogged}
          updateHeader={() => this.setState({ isLogged: false })}
        />
        <Container>
          <Switch>
            <Route
              exact
              path="/"
              render={() => (
                <Login updateHeader={() => this.setState({ isLogged: true })} />
              )}
            />
            <Route exact path="/users" component={UserList} />
            <Route path="/users/:id(\d+)" component={EditUser} />
            <Route path="*" component={NotFound} />
          </Switch>
        </Container>
      </div>
    );
  }
}

export default App;
