import axios from "axios";

export const login = (userData) => {
  console.log(userData);
  return axios.post(`https://reqres.in/api/login?delay=2`, {
    email: "eve.holt@reqres.in",
    password: "cityslicka",
  });
};

export const getUserList = (page = 1, store) => {
  return axios.get(
    //https://reqres.in/api/users?page=2&delay=2
    `https://reqres.in/api/users?page=${page}&delay=2`,
    {
      headers: {
        token: store.getState().clickState.token,
      },
    }
  );
};

export const getUserById = (id, store) => {
  return axios.get(`https://reqres.in/api/users/${id}?delay=2`, {
    headers: {
      token: store.getState().clickState.token,
    },
  });
};

export const updateUser = (userData, store) => {
  console.log(userData, store);
  return axios.put(
    `https://reqres.in/api/users/${userData.id}?delay=2`,
    {
      first_names: userData.first_name,
      last_name: userData.last_name,
    },
    {
      headers: {
        token: store.getState().clickState.token,
      },
    }
  );
};
