import React, { Component } from "react";
import "./App.scss";
import Header from "./components/header/header";
import Login from "./components/login/login";
import UserList from "./components/users/users";
import EditUser from "./components/edit-user/edit-users";
import NotFound from "./components/not-found/not-found";
import { Container } from "react-materialize";
import { Switch, Route } from "react-router-dom";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { isLogged: window.location.pathname !== "/" ? true : false };
  }

  render() {
    console.log(
      /^(\/users|\/users\/|\/users\/[0-9])$/.test(window.location.pathname)
    );
    return (
      <div>
        <Header
          isLogged={this.state.isLogged}
          updateHeader={() => this.setState({ isLogged: false })}
        />
        <Container>
          <Switch>
            <Route
              exact
              path="/"
              render={() => (
                <Login updateHeader={() => this.setState({ isLogged: true })} />
              )}
            />
            <Route exact path="/users" component={UserList} />
            <Route path="/users/:id" component={EditUser} />
            <Route path="*" component={NotFound} />
          </Switch>
        </Container>
      </div>
    );
  }
}

export default App;
