import React, { useState, useEffect } from "react";
import { Row, Col, Card, Input } from "react-materialize";
import { useStore } from "react-redux";
import axios from "axios";

const EditUser = ({ match }) => {
  const store = useStore(),
    [user, setUser] = useState({ first_name: "", last_name: "", email: "" });

  useEffect(() => {
    const loadUser = async () => {
      const response = await axios.get(
        `https://reqres.in/api/users/${match.params.id}?delay=2`,
        {
          headers: {
            token: store.getState().clickState.token,
          },
        }
      );
      setUser(response.data.data);
    };

    loadUser();
  }, [store, match]);

  const editField = (e) => {
    console.log(e);
  };

  return (
    <Row>
      <Col m={12} s={12}>
        <h5 className="subtitle">Edição de usuário</h5>
        <Card>
          <Row>
            <div className="col s6">
              <label for="first_name">First Name</label>
              <input
                placeholder="Placeholder"
                id="first_name"
                type="text"
                class="validate"
              />
            </div>
            <Input
              type="text"
              label="Nome"
              s={6}
              value={`${user.first_name} ${user.last_name}`}
              onchange={() => editField(1)}
            />
            <Input
              type="text"
              label="Email"
              s={6}
              value={user.email}
              readonly="true"
            />
          </Row>
        </Card>
      </Col>
    </Row>
  );
};

export default EditUser;
